glsl3.vert:
precision highp float;
uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
};
#if USE_BATCHING
  in float a_dyn_batch_id;
  uniform CCLocalBatched {
    highp mat4 cc_matWorlds[10];
  };
#else
uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
};
#endif
in vec3 a_position;
#if USE_SKINNING
struct StandardVertInput {
  highp vec4 position;
  vec3 normal;
  vec4 tangent;
};
in vec4 a_weights;
in vec4 a_joints;
uniform CCSkinningTexture {
  highp vec4 cc_jointsTextureInfo;
};
uniform CCSkinningAnimation {
  highp vec4 cc_jointsAnimInfo;
};
uniform sampler2D cc_jointsTexture;
#if USE_SKINNING == 1
  highp float decode32 (highp vec4 rgba) {
    rgba = rgba * 255.0;
    highp float Sign = 1.0 - step(128.0, rgba[3]) * 2.0;
    highp float Exponent = 2.0 * mod(rgba[3], 128.0) + step(128.0, rgba[2]) - 127.0;
    highp float Mantissa = mod(rgba[2], 128.0) * 65536.0 + rgba[1] * 256.0 + rgba[0] + 8388608.0;
    return Sign * exp2(Exponent - 23.0) * Mantissa;
  }
#endif
#if USE_SKINNING == 1
  mat4 getJointMatrix (float i) {
    highp float j = 12.0 * (cc_jointsAnimInfo.x * i + cc_jointsAnimInfo.y) + cc_jointsTextureInfo.z;
    highp float invSize = cc_jointsTextureInfo.y;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointsTextureInfo.x;
    y = (y + 0.5) * invSize;
    vec4 v1 = vec4(
      decode32(texture(cc_jointsTexture, vec2((x + 0.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 1.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 2.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 3.5) * invSize, y)))
    );
    vec4 v2 = vec4(
      decode32(texture(cc_jointsTexture, vec2((x + 4.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 5.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 6.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 7.5) * invSize, y)))
    );
    vec4 v3 = vec4(
      decode32(texture(cc_jointsTexture, vec2((x + 8.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 9.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 10.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 11.5) * invSize, y)))
    );
    return mat4(vec4(v1.xyz, 0.0), vec4(v2.xyz, 0.0), vec4(v3.xyz, 0.0), vec4(v1.w, v2.w, v3.w, 1.0));
  }
#elif USE_SKINNING == 2
  mat4 getJointMatrix (float i) {
    highp float j = 3.0 * (cc_jointsAnimInfo.x * i + cc_jointsAnimInfo.y) + cc_jointsTextureInfo.z;
    highp float invSize = cc_jointsTextureInfo.y;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointsTextureInfo.x;
    y = (y + 0.5) * invSize;
    vec4 v1 = texture(cc_jointsTexture, vec2((x + 0.5) * invSize, y));
    vec4 v2 = texture(cc_jointsTexture, vec2((x + 1.5) * invSize, y));
    vec4 v3 = texture(cc_jointsTexture, vec2((x + 2.5) * invSize, y));
    return mat4(vec4(v1.xyz, 0.0), vec4(v2.xyz, 0.0), vec4(v3.xyz, 0.0), vec4(v1.w, v2.w, v3.w, 1.0));
  }
#endif
mat4 skinMatrix () {
  return getJointMatrix(a_joints.x) * a_weights.x
    + getJointMatrix(a_joints.y) * a_weights.y;
    + getJointMatrix(a_joints.z) * a_weights.z;
    + getJointMatrix(a_joints.w) * a_weights.w;
}
void CCSkin (inout vec4 position) {
  mat4 m = skinMatrix();
  position = m * position;
}
void CCSkin (inout StandardVertInput attr) {
  mat4 m = skinMatrix();
  attr.position = m * attr.position;
  attr.normal = (m * vec4(attr.normal, 0.0)).xyz;
  attr.tangent.xyz = (m * vec4(attr.tangent.xyz, 0.0)).xyz;
}
#endif
#if USE_VERTEX_COLOR
  in vec4 a_color;
  out vec4 v_color;
#endif
#if USE_TEXTURE
  in vec2 a_texCoord;
  out vec2 v_uv;
  uniform TexCoords {
    vec4 tilingOffset;
  };
#endif
vec4 vert () {
  vec4 position;
  position = vec4(a_position, 1.0);
  #if USE_SKINNING
    CCSkin(position);
  #endif
  mat4 matWorld;
  #if USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id + 0.5)];
  #else
    matWorld = cc_matWorld;
  #endif
  #if USE_TEXTURE
    v_uv = a_texCoord;
    #if FLIP_UV
      v_uv.y = 1.0 - v_uv.y;
    #endif
    v_uv = v_uv * tilingOffset.xy + tilingOffset.zw;
  #endif
  #if USE_VERTEX_COLOR
    v_color = a_color;
  #endif
  return cc_matProj * (cc_matView * matWorld) * position;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision mediump float;
uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
};
vec3 SRGBToLinear (vec3 gamma) {
  return gamma * gamma;
}
vec4 CCFragOutput (vec4 color) {
  #if CC_USE_HDR
    color.rgb = mix(color.rgb, SRGBToLinear(color.rgb) * cc_exposure.w, vec3(cc_exposure.z));
	#endif
	return color;
}
#if USE_ALPHA_TEST
#endif
#if USE_TEXTURE
  in vec2 v_uv;
  uniform sampler2D mainTexture;
#endif
uniform Constant {
  vec4 mainColor;
  vec4 colorScaleAndCutoff;
};
#if USE_VERTEX_COLOR
  in vec4 v_color;
#endif
vec4 frag () {
  vec4 o = mainColor;
  o.rgb *= colorScaleAndCutoff.xyz;
  #if USE_VERTEX_COLOR
    o *= v_color;
  #endif
  #if USE_TEXTURE
    o *= texture(mainTexture, v_uv);
  #endif
  #if USE_ALPHA_TEST
    if (o.ALPHA_TEST_CHANNEL < colorScaleAndCutoff.w) discard;
  #endif
  return CCFragOutput(o);
}
out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

